package com.pearl.epub.util;

// TODO: Auto-generated Javadoc
/**
 * The Class StringUtil.
 */
public class StringUtil {

    /**
     * Null2str.
     *
     * @param str the str
     * @return the string
     */
    public static String null2str(String str) {
	if (str == null) {
	    return "";
	} else {
	    return str;
	}
    }
}
